import './App.css';
import React, { Component } from 'react';
// import LoginRegister from './components/LoginRegister';
import Logo from './components/Logo';
import Nav from './components/Nav';
import Menu from './components/Menu';
// import Dish from './components/Dish';
import Order from './components/Order';
// import Payment from './components/Payment';


let baseUrl = process.env.REACT_APP_BASEURL

class App extends Component {
  constructor(){
    super()
    this.state = {
      username: '',
      password: '',
      email: '',
      phone_num: '',
      address: '',
      payment_info: {},
      userLoggedIn: false,
      modalOpen: false,
      orders: [],
      currentOrderId: null,
      orderToBeEdited: {},
      dishes: [],
    }
  }

  getDishes = () => {
    console.log('hit getDishes')
    fetch(baseUrl + '/dishes/', {
      credentials: 'include'
    })
    .then (res => {
      if(res.status === 200) {
        console.log('游보 fetch index dishes successful! 游보')
        return res.json()
      } else {
        console.log('fetch index dishes failed...游눢')
        return[]
      }
    })
    .then(data => {
      console.log('data: ' + data)
      this.setState({
        dishes: data.data
      })
      console.log('new data: ' + data)
    })
  } 

  newOrder = (newOrder) => {
    const copyOrders = [...this.state.orders]
    copyOrders.push(newOrder)
    this.setState({
      orders: copyOrders,
      currentOrderId: newOrder.id,
    })
  }

  addDishToOrder =(dish_id)=> {
    console.log('dish id: ' + dish_id,'order id :' + this.currentOrderId)
    if(this.currentOrderId === null){
      // add dish to order here
      this.newOrder()
      const copyOrders = [...this.state.orders]
      const dishToAdd = this.state.dishes.find(dish => dish.dish_id === dish_id)
      console.log(dishToAdd)
      copyOrders.push(dishToAdd)


    }else {
      // add dish to order here
    }
  }

  componentDidMount(){
    this.getDishes()
  }

  render(){


    return (
      <div className="App">
      <Logo />
      <Nav />

      <div className="bottom-container">
        <Menu 
          dishes = {this.state.dishes}
        />
        <Order />
      </div>
      {/* <LoginRegister /> */}
      {/* <Dish /> */}
      {/* <Payment /> */}
    </div>
    );
  }
}

export default App;
